import { useDispatch, useSelector } from 'react-redux'; // Import useDispatch from react-redux
import { fetchAsync } from "./productsSlice";
import '../products/Card.css'
export function Products() {
  const dispatch = useDispatch(); // Initialize dispatch using useDispatch
  const products=useSelector(state=>state.product.products)
  return (
    <>
      <button onClick={() => dispatch(fetchAsync())}>fetchAsync</button>
      {
        products.map((product)=>(
          <div className="card">
        <img src={product.thumbnail} alt={product.title} style={{ width: '100%' }} /> {/* Style should be an object */}
        <h1>Tailored Jeans</h1>
        <p className="price">{product.price}</p>
        <p>{product.des}</p>
        <p>
          <button>Add to Cart</button>
        </p>
      </div>
        ))
      }
    </>
  );
}
